mapper.select:
SELECT id, VID, PID, T, C
FROM battery_data
WHERE PID = #{pid}

SELECT EXISTS (
    SELECT 1 FROM battery_data
    WHERE PID = #{pid}
    AND T = #{T}
    AND C = #{C}
    AND id = (
        SELECT id FROM battery_data 
        WHERE PID = #{pid} 
        ORDER BY id DESC 
        LIMIT 1
    )
)
